/*Table structure for table `bms_book` */

DROP TABLE IF EXISTS `bms_book`;

CREATE TABLE `bms_book` (
  `id` bigint(255) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL DEFAULT 'postbird',
  `reg_time_stamp` bigint(255) NOT NULL DEFAULT '2010101',
  `reg_time_date` varchar(255) NOT NULL DEFAULT '2016-01-01',
  `content` text NOT NULL,
  `author` varchar(255) NOT NULL DEFAULT 'postbird',
  `guid` varchar(255) NOT NULL DEFAULT '0000-0000-0000-0000',
  `tag` varchar(255) NOT NULL DEFAULT '0',
  `book_time_stamp` bigint(255) NOT NULL DEFAULT '2010101',
  `book_time_date` varchar(255) NOT NULL DEFAULT '2016-01-01',
