) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC;

/*Data for the table `bms_book` */

insert  into `bms_book`(`id`,`title`,`reg_time_stamp`,`reg_time_date`,`content`,`author`,`guid`,`tag`,`book_time_stamp`,`book_time_date`,`description`,`image`) values (2,'Scala程序设计',1473935336,'2016-09-15 18:09:56','&lt;p style=&quot;margin:0px 0px 15px;padding:0px;line-height:24px;clear:both;color:#666666;font-family:宋体, 微软雅黑, arial;font-size:14px;font-style:normal;font-variant-ligatures:normal;font-variant-caps:normal;font-weight:normal;letter-spacing:normal;orphans:2;text-align:start;text-indent:0px;text-transform:none;white-space:normal;widows:2;word-spacing:0px;-webkit-text-stroke-width:0px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;strong&gt;Scala程序设计(第2版)&lt;/strong&gt;通过大量的代码示例，全面介绍Scala这门针对JVM的编程语言，向读者展示了如何高效地利用Scala语言及其生态系统，同时解释了为何Scala是开发高扩展性、以数据为中心的应用程序的理想语言。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin:0px 0px 15px;padding:0px;line-height:24px;clear:both;color:#666666;font-family:宋体, 微软雅黑, arial;font-size:14px;font-style:normal;font-variant-ligatures:normal;font-variant-caps:normal;font-weight:normal;letter-spacing:normal;orphans:2;text-align:start;text-indent:0px;text-transform:none;white-space:normal;widows:2;word-spacing:0px;-webkit-text-stroke-width:0px;background-color:#FFFFFF;&quot;&gt;\r\n	本书既适合Scala初学者入门，也适合经验丰富的Scala开发者参考。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin:0px 0px 15px;padding:0px;line-height:24px;clear:both;color:#666666;font-family:宋体, 微软雅黑, arial;font-size:14px;font-style:normal;font-variant-ligatures:normal;font-variant-caps:normal;font-weight:normal;letter-spacing:normal;orphans:2;text-align:start;text-indent:0px;text-transform:none;white-space:normal;widows:2;word-spacing:0px;-webkit-text-stroke-width:0px;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;color:#ff0000;&quot;&gt;Scala程序设计(第2版) 目录&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin:0px 0px 15px;padding:0px;line-height:24px;clear:both;color:#666666;font-family:宋体, 微软雅黑, arial;font-size:14px;font-style:normal;font-variant-ligatures:normal;font-variant-caps:normal;font-weight:normal;letter-spacing:normal;orphans:2;text-align:start;text-indent:0px;text-transform:none;white-space:normal;widows:2;word-spacing:0px;-webkit-text-stroke-width:0px;background-color:#FFFFFF;&quot;&gt;\r\n	第1章　零到六十：Scala简介　　1&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第2章　更简洁，更强大　　28&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第3章　要点详解　　60&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第4章　模式匹配　　86&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第5章　隐式详解　　112&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第6章　Scala函数式编程　　147&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第7章　深入学习for推导式　　189&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第8章　Scala面向对象编程　　210&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第9章　特征　　 233&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第10章　Scala对象系统（I）　　246&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第11章　Scala对象系统（II）　　269&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第12章　Scala集合库　　288&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第13章　可见性规则　　301&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第14章　Scala类型系统（I）　　314&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第15章　Scala类型系统（II）　　336&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第16章　高级函数式编程　　352&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第17章　并发工具　　365&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第18章　Scala与大数据　　386&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第19章　Scala动态调用　　396&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第20章　Scala的领域特定语言　　403&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第21章　Scala工具和库　　414&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第22章　与Java的互操作　　430&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第23章　应用程序设计　　435&lt;span class=&quot;Apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;br /&gt;\r\n第24章　元编程：宏与反射　　454\r\n&lt;/p&gt;','Postbird','17620AA714FE20C3333802BA67277AD1','scala,大数据,云计算',1473935336,'2016-9-01','Scala程序设计(第2版)通过大量的代码示例，全面介绍Scala这门针对JVM的编程语言，向读者展示了如何高效地利用Scala语言及其生态系统，同时解释了为何Scala是开发高扩展性、以数据为中心的应用程序的理想语言。\r\n\r\n本书既适合Scala初学者入门，也适合经验丰富的Scala开发者参考。','b1473935336.jpg'),(3,'Hadoop实战（第二版）',1473952340,'2016-09-15 23:09:20','&lt;h2&gt;\r\n	&lt;span class=&quot;&quot;&gt;内容简介&lt;/span&gt; &amp;nbsp;·&amp;nbsp;·&amp;nbsp;·&amp;nbsp;·&amp;nbsp;·&amp;nbsp;·\r\n&lt;/h2&gt;\r\n&lt;div class=&quot;indent&quot; id=&quot;link-report&quot;&gt;\r\n	&lt;span class=&quot;short&quot;&gt; \r\n	&lt;div class=&quot;intro&quot;&gt;\r\n		&lt;p&gt;\r\n			本书是一本系统且极具实践指导意义的Hadoop工具书和参考书。内容全面，对Hadoop整个技术体系进行了全面的讲解，不仅包括\r\nHDFS和MapReduce这两大核心内容，而且还包括Hive、HBase、Mahout、Pig、ZooKeeper、Avro、Chukwa等与\r\nHadoop相关的子项目的内容。实战性强，为各个知识点精心设计了大量经典的小案例，易于理解，可操作性强。\r\n		&lt;/p&gt;\r\n		&lt;p&gt;\r\n			全书一共18章：第\r\n1章全面介绍了Hadoop的概念、优势、项目结构、体系结构，以及它与分布式计算的关系；第2章详细讲解了Hadoop集群的安装和配置，以及常用的日\r\n志分析技巧；第3章分析了Hadoop在Yahoo！、eBay、Facebook和百度的应用案例，以及Hadoop平台上海量数据的排序；第4-7章\r\n深入地讲解了MapReduce计算模型、MapReduce应用的开发方法、Map...\r\n		&lt;/p&gt;\r\n		&lt;p&gt;\r\n			&lt;a class=&quot;j a_show_full&quot;&gt;(展开全部)&lt;/a&gt; \r\n		&lt;/p&gt;\r\n	&lt;/div&gt;\r\n&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;h2&gt;\r\n	&lt;span class=&quot;&quot;&gt;作者简介&lt;/span&gt; &amp;nbsp;·&amp;nbsp;·&amp;nbsp;·&amp;nbsp;·&amp;nbsp;·&amp;nbsp;·\r\n&lt;/h2&gt;\r\n&lt;div class=&quot;indent &quot;&gt;\r\n	&lt;div class=&quot;&quot;&gt;\r\n		&lt;div class=&quot;intro&quot;&gt;\r\n			&lt;p&gt;\r\n				陆嘉恒，中国人民大学副教授，新加坡国立大学博士，美国加利福尼亚大学尔湾分校(University of California, \r\nIrvine) \r\n博士后。专注于云计算及其相关技术的研究，对Hadoop有较深入的研究，积累了丰富的实践经验。对分布式计算和海量数据处理有深刻的认识，主持并完成了\r\n多个国家863和自然科学基金项目的研究与实施。2009年入选新世纪优秀人才，2010年入选北京科技新星。主持《云计算概论》课程获教育部-IBM精\r\n品课程称号。\r\n			&lt;/p&gt;\r\n		&lt;/div&gt;\r\n	&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;h2&gt;\r\n	&lt;span class=&quot;&quot;&gt;目录&lt;/span&gt; &amp;nbsp;·&amp;nbsp;·&amp;nbsp;·&amp;nbsp;·&amp;nbsp;·&amp;nbsp;·\r\n&lt;/h2&gt;\r\n前言&lt;br /&gt;\r\n第1章 Hadoop简介&lt;br /&gt;\r\n第2章 Hadoop的安装与配置&lt;br /&gt;\r\n第3章 Hadoop应用案例分析&lt;br /&gt;\r\n第4章 MapReduce计算模型&lt;br /&gt;\r\n第5章 开发MapReduce应用程序&lt;br /&gt;\r\n第6章 MapReduce应用案例&lt;br /&gt;\r\n第7章 MapReduce工作机制&lt;br /&gt;\r\n第8章 HadoopI/O&lt;br /&gt;\r\n第9章 HDFS详解&lt;br /&gt;\r\n第10章 Hadoop的管理&lt;br /&gt;\r\n第11章 Hive详解&lt;br /&gt;\r\n第12章 HBase详解&lt;br /&gt;\r\n第13章 Mahout详解&lt;br /&gt;\r\n第14章 Pig详解&lt;br /&gt;\r\n第15章 ZooKeepet详解&lt;br /&gt;\r\n第16章 Avro详解&lt;br /&gt;\r\n第17章 Chukwa详解&lt;br /&gt;\r\n第18章 Hadoop的常用插件与开发&lt;br /&gt;\r\n附录A 云计算在线检测平台','陆嘉恒','B5F3F96D80C8C89DF883F83351D13245','hadoop,大数据,云计算,hive,hbase,mapreduce,hdfs',1473957124,'2016-09-05','本书是一本系统且极具实践指导意义的Hadoop工具书和参考书。内容全面，对Hadoop整个技术体系进行了全面的讲解，不仅包括HDFS和MapReduce这两大核心内容，而且还包括Hive、HBase、Mahout、Pig、ZooKeeper、Avro、Chukwa等与Hadoop相关的子项目的内容						','b1473952340.jpg'),(5,'Storm实战-构建大数据实时计算',1473957276,'2016-09-16 00:09:36','&lt;div class=&quot;para&quot;&gt;\r\n	&lt;h2&gt;\r\n		&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;介绍：&lt;br /&gt;\r\n&lt;/span&gt; \r\n	&lt;/h2&gt;\r\n	&lt;p&gt;\r\n		&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;随着&lt;/span&gt;&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/subview/6954399/13647476.htm&quot; data-lemmaid=&quot;1356941&quot;&gt;&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;大数据&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;实时处理需求的强劲增长，&lt;/span&gt;&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/1012011.htm&quot;&gt;&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;Storm&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;的出现填补了大数据处理生态系统的缺失，并被越来越多的公司所采用。阿里巴巴集团数据平台事业部商家数据业务部正是最早使用Storm的技术团队之一。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;《Storm实战：构建大数据实时计算 》是一本系统并且具有实践指导意义的Storm工具书和参考书，对Storm整个技术体系进行了全面的讲解，不仅包括对基本概念、特性的介绍，也涵盖了一些原理说明。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;实战性很强，各章节都提供了一些小案例，同时对于本地，以及集群环境的部署有详细介绍，易于理解，操作性强。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;p&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;《Storm\r\n实战：构建大数据实时计算 \r\n》一共分为10章：第1章全面介绍了Storm的特性、能解决什么问题，以及和其他流计算系统的对比；第2章通过实际运行一个简单的例子，以及介绍本地环\r\n境和集群环境的搭建，让读者对Storm有了直观的认识；第3章深入讲解了Storm的基本概念，同时实现一个Topology运行；第4章和第5章阐述\r\n了Storm的并发度、可靠处理的特性；第6章~第8章详细而系统地讲解了几个高级特性：事务、DRPC和Trident；第9章以实例的方式讲解了\r\nStorm在实际业务场景中的应用；第10章总结了几个在大数据场景应用过程中遇到的经典问题，以及详细的排查过程。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;Storm以其简单、灵活、健壮而著称。随着大数据实时处理需求的强劲增长，Storm的出现填补了大数据处理生态系统的缺失，并被越来越多的公司所采用。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;《Storm实战：构建大数据实时计算 \r\n》来自阿里巴巴集团数据平台事业部商家数据业务部一线经验的分享。团队专注于“数据让生意更简单”的大数据应用使命，一直致力于解决商家的大数据应用需\r\n求。并开发出量子恒道店铺经、江湖策、御膳房等重量级大数据开发、计算和分析工具。团队很早便开始使用Storm来处理实时大数据，结合实际的应用场景，\r\n积累了丰富的实践经验。《Storm实战：构建大数据实时计算》正是这些经验和心得的结晶，它一一解答了是Storm是什么，怎么做，如何做更好等关键的\r\n实际问题，对于从事大数据处理与分析的读者而言，是一本相当有价值的学习参考手册。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;阿里巴巴集团技术丛书是抱持着为工程师服务的理念诞生的。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;阿里的快速发展，使我们有机会见证技术变革的强大力量。在这过程中，我们\r\n经历了技术的变迁、兴衰、成败，既有收获也有教训。如今，阿里已成为一家令人瞩目的互联网公司，站在这样的时点上，回顾过去，放眼四周，我们深刻地觉得有\r\n必要将沉淀下来的一线经验与智慧，结集出版，理由有三。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;1．回报。我们获得的种种经验与教训，只有在阿里这样快速成长、体量巨大\r\n的公司中才有机会亲历。这样的技术环境并不是每个工程师都有机缘遇到的。这些经历帮助了我们个人能力的提升，也帮助阿里成为一家技术实力过硬的公司。我们\r\n愿意把这些内容分享出来，帮助即将或已经面临类似问题的朋友们，如果他们可以从阅读中获得一些启发而少走弯路的话，将是我们最大的欣慰。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;2．交流。在这套丛书里，我们同样会抛出我们的困惑、问题，借助丛书的平台，扩大工程师沟通的圈子，以书会友，共同探索、学习、提高。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;3．温故知新。以结集的形式，将以往的经验与知识固化下来，既可便于以后查阅，也可以帮助我们系统地梳理思路，更为全面地审视、洞察、并理解过去发生的种种，为未来的变化做好更充分、扎实的准备。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;基于以上的考虑，阿里巴巴集团技术丛书将立足于实践，着眼于问题，从工程师的视角解读技术的内涵。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;让思考成为我们生活的乐趣，让阅读成为我们进步的阶梯！&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;内容提要&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;随着大数据实时处理需求的强劲增长，Storm的出现填补了大数据处理生态系统的缺失，并被越来越多的公司所采用。阿里巴巴集团数据平台事业部商家数据业务部正是最早使用Storm的技术团队之一。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;《Storm实战：构建大数据实时计算 》是一本系统并且具有实践指导意义的Storm工具书和参考书，对Storm整个技术体系进行了全面的讲解，不仅包括对基本概念、特性的介绍，也涵盖了一些原理说明。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;实战性很强，各章节都提供了一些小案例，同时对于本地，以及集群环境的部署有详细介绍，易于理解，操作性强。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;《Storm实战：构建大数据实时计算 \r\n》一共分为10章：第1章全面介绍了Storm的特性、能解决什么问题，以及和其他流计算系统的对比；第2章通过实际运行一个简单的例子，以及介绍本地环\r\n境和集群环境的搭建，让读者对Storm有了直观的认识；第3章深入讲解了Storm的基本概念，同时实现一个Topology运行；第4章和第5章阐述\r\n了Storm的并发度、可靠处理的特性；第6章~第8章详细而系统地讲解了几个高级特性：事务、DRPC和Trident；第9章以实例的方式讲解了\r\nStorm在实际业务场景中的应用；第10章总结了几个在大数据场景应用过程中遇到的经典问题，以及详细的排查过程。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;h2 class=&quot;title-text&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;作者简介&lt;/span&gt; \r\n&lt;/h2&gt;\r\n&lt;a style=&quot;display:block;&quot; class=&quot;edit-icon j-edit-link&quot; data-edit-dl=&quot;3&quot;&gt;&lt;em class=&quot;cmn-icon wiki-lemma-icons wiki-lemma-icons_edit-lemma&quot;&gt;&lt;/em&gt;&lt;/a&gt; \r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;商家数据业务部, \r\n隶属于阿里巴巴集团数据平台事业部，致力于“数据让生意更简单”的大数据应用使命。几年来，一直专注于解决商家的大数据应用需求，通过量子恒道店铺经已支\r\n撑起超过400万商家的日常经营决策，并为100万商家提供淘宝官方流量管理工具——江湖策，让深度探索、优化流量成为可能。2013年开创性地推出云数\r\n据平台——御膳房，作为完善的大数据开放、计算和分析云平台，打通了“阿里大数据”与“开放平台和服务市场体系”，为淘宝生态中的ISV和卖家提供海量丰\r\n富的阿里大数据、强大稳定的云计算处理能力和安全的数据应用开发容器，支持淘宝生态圈发展基于大数据分析的创新应用，深度助力卖家降低经营成本、提升效\r\n率，已为数百万的商家提供经营链路中的大数据应用解决方案。&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt; 　　支撑这些的，是对大数据的分布式计算、仓库建设、数据挖掘、高并发网站构建、数据可视化等各个领域前沿技术的深入探索和应用。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;h2 class=&quot;title-text&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;前言&lt;/span&gt; \r\n&lt;/h2&gt;\r\n&lt;a style=&quot;display:block;&quot; class=&quot;edit-icon j-edit-link&quot; data-edit-dl=&quot;4&quot;&gt;&lt;em class=&quot;cmn-icon wiki-lemma-icons wiki-lemma-icons_edit-lemma&quot;&gt;&lt;/em&gt;&lt;/a&gt; \r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;实时流计算&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;互联网从诞生的一刻起，对世界的最大改变就是让信息能够实时交互，从而大\r\n大提高各个环节的效率。正因为大家对信息实时响应、实时交互的需求，软件行业除了个人操作系统之外，数据库（更精确地说是关系型数据库）应该是软件行业发\r\n展最快、收益最为丰厚的产品了。记得20 世纪90年代，很多银行别说实时转账，连实时查询都做不到，但是数据库和高速网络改变了这个情况。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;互联网的进一步发展，从Portal 信息浏览型到Search \r\n信息搜索型再到SNS \r\n关系交互传递型，以及电子商务、互联网旅游生活产品等，将人们生活中的流通环节在线化。对效率的要求让大家对实时性的要求进一步提升，而信息的交互和沟通\r\n正在从点对点向信息链甚至信息网的方向发展，这样必然带来数据在各个维度的交叉关联，数据爆炸已不可避免。因此流式处理加NoSQL \r\n产品应运而生，分别解决实时处理框架和数据大规模存储计算的问题。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;早在2000 年初，诸如UC \r\n伯克利、斯坦福等大学就开始了对流式数据处理的研究，但是由于更多地关注于金融行业的业务场景或者互联网流量监控的业务场景，以及当时互联网数据场景的限\r\n制，造成了研究多是基于对传统数据库处理的流式化，对流式框架本身的研究偏少。目前这样的研究逐渐没有了声音，工业界将更多的精力转向了实时数据库。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;2010 年Yahoo！对S4 的开源，2011 年Twitter \r\n对Storm的开源，改变了这个现状。以前互联网的开发人员在做一个实时应用的时候，除了要关注应用逻辑计算处理本身，还要为数据的实时流转、交互、分布\r\n大伤脑筋。现在的情况却大为不同，以Storm 为例，开发人员可以快速搭建一套健壮、易用的实时流处理框架，配合SQL 产品或者NoSQL \r\n产品或者MapReduce 计算平台，就可以以低成本做出很多以前很难想象的实时产品，比如量子恒道品牌旗下的多个产品就是构建在Storm \r\n实时流处理平台上的。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;本书是一本对Storm 的基础介绍手册，但是我们也希望它不仅仅是一本Storm 的使用手册，我们会在其中加入更多在实际数据生产过程中的经验和应用架构，最终的目的是帮助所有愿意使用实时流处理框架的技术同仁，同时也默默地改变这个世界。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;在本书即将出版之际，Storm 已经成功发布了0.9.0 \r\n版本，追加了一些新的特性，如使用Netty 作为新的消息传输层、提供日志查看UI \r\n等，同时修复了大量跟稳定性相关的BUG。本次发布对茁壮成长的Storm 来说是一次巨大的进步。新版本的Storm \r\n在系统结构及使用方式方面，并没有太多变化，本书可以帮助你快速掌握应用Storm 的知识和技能。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;span style=&quot;font-family:Microsoft YaHei;line-height:1.5;&quot;&gt;本书由团队中多位同学先后参与合作完成，为体现阿里巴巴的文化，这里列出\r\n所有涉及同学的花名：张中、太奇、鸣世、曦轩、呜珂、民瞻、九翎、渊虹、国相、晨炫、木晗、毅山、宋智、澄苍，是大家的合作与努力才让此书得以成行。同时\r\n感谢刘皎等编辑的辛苦工作，是你们把这本书呈献给读者，感谢你们！同样要感谢支持我们工作的同事们：冰夷、王贲，有你们的帮助和支持才让我们有决心和毅力\r\n来完成这项工作。&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;','阿里巴巴','E20A3F9794F9A44B072436AA61E8F64A','storm,大数据,云计算,实时大数据计算,流计算',1473957356,'2016-9-15','《Storm实战：构建大数据实时计算 》是一本系统并且具有实践指导意义的Storm工具书和参考书，对Storm整个技术体系进行了全面的讲解，不仅包括对基本概念、特性的介绍，也涵盖了一些原理说明。												','b1473957276.jpg'),(6,'深入理解Linux内核',1474029162,'2016-09-16 20:09:42','&lt;h2 class=&quot;title-text&quot;&gt;\r\n	图书信息\r\n&lt;/h2&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	书 名: 深入理解LINUX内核 (Understanding the Linux Kernel)\r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;div class=&quot;lemma-picture text-pic layout-right&quot; style=&quot;width:165px;float:right;&quot;&gt;\r\n		&lt;a class=&quot;image-link&quot; href=&quot;http://baike.baidu.com/pic/%E6%B7%B1%E5%85%A5%E7%90%86%E8%A7%A3LINUX%E5%86%85%E6%A0%B8/6153389/0/cc506c8bc27f9437c8fc7ab1?fr=lemma&amp;amp;ct=single&quot; target=&quot;_blank&quot; title=&quot;&quot; style=&quot;width:165px;height:220px;&quot;&gt; &lt;br /&gt;\r\n&lt;/a&gt; \r\n	&lt;/div&gt;\r\n作　者：(美)博韦等 (DanielP. Bovet)\r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	中文版译者： 陈莉群 &lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/299708.htm&quot;&gt;冯锐&lt;/a&gt; 牛欣源\r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	出版社： &lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/162427.htm&quot;&gt;中国电力出版社&lt;/a&gt;\r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	出版时间： 2008\r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/20171.htm&quot;&gt;ISBN&lt;/a&gt;: 9787508353944\r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	开本： 16\r\n&lt;/div&gt;\r\n&lt;div class=&quot;para&quot;&gt;\r\n	&lt;p&gt;\r\n		定价: 98.00 元\r\n	&lt;/p&gt;\r\n	&lt;p&gt;\r\n		&lt;h2 class=&quot;title-text&quot;&gt;\r\n			作者简介\r\n		&lt;/h2&gt;\r\n&lt;a style=&quot;display:block;&quot; class=&quot;edit-icon j-edit-link&quot; data-edit-dl=&quot;2&quot;&gt;&lt;em class=&quot;cmn-icon wiki-lemma-icons wiki-lemma-icons_edit-lemma&quot;&gt;&lt;/em&gt;&lt;/a&gt; \r\n		&lt;div class=&quot;para&quot;&gt;\r\n			Bovet（博韦），计算机科学专业博士，意大利罗马大学Torvergata分校全职教授。\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			切&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/297181.htm&quot;&gt;萨蒂&lt;/a&gt;数学和计算机科学博士，&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/view/294558.htm&quot;&gt;罗马大学&lt;/a&gt;Torvergata分校工程学院计算机科学系&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/subview/5032743/5035856.htm&quot; data-lemmaid=&quot;2178444&quot;&gt;助理研究员&lt;/a&gt;。\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;anchor-list&quot;&gt;\r\n			&lt;a name=&quot;3&quot;&gt;&lt;/a&gt; &lt;a name=&quot;sub2501578_3&quot;&gt;&lt;/a&gt; &lt;a name=&quot;目录&quot;&gt;&lt;/a&gt; \r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para-title level-2&quot;&gt;\r\n			&lt;h2 class=&quot;title-text&quot;&gt;\r\n				目录\r\n			&lt;/h2&gt;\r\n&lt;a style=&quot;display:block;&quot; class=&quot;edit-icon j-edit-link&quot; data-edit-dl=&quot;3&quot;&gt;&lt;em class=&quot;cmn-icon wiki-lemma-icons wiki-lemma-icons_edit-lemma&quot;&gt;&lt;/em&gt;&lt;/a&gt; \r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			前言\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			第一章绪论\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/subview/1634/7331705.htm&quot; data-lemmaid=&quot;27050&quot;&gt;Linux&lt;/a&gt;与其他类&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/subview/8095/8095.htm&quot; data-lemmaid=&quot;219943&quot;&gt;Unix&lt;/a&gt;内核的比较\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			硬件的依赖性\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			Linux版本\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			操作系统基本概念\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			Unix文件系统概述\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			Unix内核概述\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			第二章&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/subview/757253/757253.htm&quot; data-lemmaid=&quot;1012006&quot;&gt;内存寻址&lt;/a&gt;\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			内存地址\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			硬件中的分段\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			Linux中的分段\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			硬件中的分页\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			Linux中的分页\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			第三章进程\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			进程、&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/subview/2136774/2136774.htm&quot; data-lemmaid=&quot;580239&quot;&gt;轻量级进程&lt;/a&gt;和&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/subview/1053/1053.htm&quot; data-lemmaid=&quot;103101&quot;&gt;线程&lt;/a&gt;\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			进程描述符\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/subview/3047148/3047148.htm&quot; data-lemmaid=&quot;4566793&quot;&gt;进程切换&lt;/a&gt;\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			创建进程\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			撤消进程\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			第四章中断和异常\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			中断信号的作用\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			中断和异常\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			中断和&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/subview/1072586/1072586.htm&quot; data-lemmaid=&quot;6250107&quot;&gt;异常处理&lt;/a&gt;程序的&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/subview/928817/928817.htm&quot; data-lemmaid=&quot;3863565&quot;&gt;嵌套&lt;/a&gt;执行\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			初始化&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/subview/2224051/2224051.htm&quot; data-lemmaid=&quot;1907776&quot;&gt;中断描述符表&lt;/a&gt;\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			异常处理\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			中断处理\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			&lt;a target=&quot;_blank&quot; href=&quot;http://baike.baidu.com/subview/197370/197370.htm&quot; data-lemmaid=&quot;3572546&quot;&gt;软中断&lt;/a&gt;及tasklet\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			工作队列\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			从中断和异常返回\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			第五章内核同步\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;para&quot;&gt;\r\n			内核如何为不同的请求提