/*Table structure for table `bms_notice` */

DROP TABLE IF EXISTS `bms_notice`;

CREATE TABLE `bms_notice` (
  `id` bigint(255) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL DEFAULT '最新公告',
  `content` text NOT NULL,
  `time_stamp` bigint(255) NOT NULL DEFAULT '20160101',
  `time_date` varchar(255) NOT NULL DEFAULT '2016-01-01',
  `author` varchar(255) NOT NULL DEFAULT '系统管理员',
  `guid` varchar(255) NOT NULL DEFAULT '0000000000000000',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

